import './css/App.css';
import Header from './Header';
import Footer from './Footer';
import CardVans from './CardVans';
import { json } from 'react-router-dom';

function Vans(data) {
    data = [
        {
            "image": "./css/image/vans1.png",
            "title": "Modest Explorer",
            "price": "$60",
            "unit" : "day",
            "style": "Simple"
        },
        {
            "image": "./css/image/vans2.png",
            "title": "Beach Bum",
            "price": "$80",
            "unit" : "day",
            "style": "Rugged"
        },
        {
            "image": "./css/image/vans3.png",
            "title": "Reliable Red",
            "price": "$100",
            "unit" : "day",
            "style": "Luxury"
        },
        {
            "image": "./css/image/vans4.png",
            "title": "Dreamfinder",
            "price": "$65",
            "unit" : "day",
            "style": "Simple"
        },
        {
            "image": "./css/image/vans5.png",
            "title": "The Cruiser",
            "price": "$120",
            "unit" : "day",
            "style": "Luxury"
        },
        {
            "image": "./css/image/vans6.png",
            "title": "Green Wonder",
            "price": "$70",
            "unit" : "day",
            "style": "Rugged"
        }
    ]

    function getClickTag(){
        document.addEventListener('click', (e)=>{
            e.preventDefault();
            console.log(e.target)
            if(e.target.tagName === 'SPAN'){
                e.target.style.border = '2px solid black';
                console.log(e.target.className)
                FilterCard(e.target.className)
            }
            else if(e.target.tagName === 'IMG') {
                console.log(e.target.alt)
            }
            else if(e.target.tagName === 'BUTTON'){
                let tag_box = document.getElementsByClassName('tag-box');
                for(let i = 0; i < tag_box.length; i++){
                    tag_box[i].style.border = 'none'
                }
            }
            else (
                console.log("no choice selected")
            )
        });
    }

    function FilterCard(value){
        let style  = data.find(el => el.code === value)["style"]
        
        let card = document.getElementsByClassName('card')
        for (let i = 0; i < card.length; i++) {
            let target = card[i].documentElement.getElementsByClassName(value)
            console.log(target)
        }
    }

    return(
        <div>
            <Header/>
            <div className='van-container' onclick = {getClickTag(this)}>
                <h1>Explore our van options</h1>
                <div className='tag-items'>
                    <span className='tag-box'>Simple</span>
                    <span className='tag-box'>Luxury</span>
                    <span className='tag-box'>Rugged</span>
                    <button className='tag-button'>Clear filters</button>
                </div>
                <div className='grid'>   
                    {data.map((vanData) =>(
                        <CardVans data = {vanData}/>
                    ))}                
                   
                </div>
            </div>
            <Footer/>
        </div>
    )
}

export default Vans
